import React, { useState } from "react";

export default function FondeoSetup({ onSetupComplete, onBack }) {
  const [accountSize, setAccountSize] = useState("");
  const [riskPercent, setRiskPercent] = useState("");
  const [tradesPerDay, setTradesPerDay] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!accountSize || !riskPercent || !tradesPerDay) {
      alert("Por favor completa todos los campos.");
      return;
    }

    onSetupComplete({
      accountSize: parseFloat(accountSize),
      riskPercent: parseFloat(riskPercent),
      tradesPerDay: parseInt(tradesPerDay),
    });
  };

  return (
    <div className="min-h-screen bg-black text-white p-6">
      <div className="max-w-xl mx-auto bg-gray-900 p-6 rounded-xl shadow-lg border border-gray-700">
        <button
          onClick={onBack}
          className="mb-4 text-indigo-400 hover:underline text-sm"
        >
          ‚Üê Volver al Dashboard
        </button>

        <h2 className="text-2xl font-bold mb-4 text-indigo-400">üîß Configuraci√≥n de Prueba de Fondeo</h2>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block mb-1 text-sm text-gray-300">Tama√±o de la cuenta ($)</label>
            <input
              type="number"
              value={accountSize}
              onChange={(e) => setAccountSize(e.target.value)}
              className="w-full p-2 rounded bg-gray-800 text-white border border-gray-700"
              placeholder="Ej: 50000"
            />
          </div>

          <div>
            <label className="block mb-1 text-sm text-gray-300">% de riesgo por trade</label>
            <input
              type="number"
              step="0.1"
              value={riskPercent}
              onChange={(e) => setRiskPercent(e.target.value)}
              className="w-full p-2 rounded bg-gray-800 text-white border border-gray-700"
              placeholder="Ej: 1"
            />
          </div>

          <div>
            <label className="block mb-1 text-sm text-gray-300">Cantidad recomendada de trades por d√≠a</label>
            <input
              type="number"
              value={tradesPerDay}
              onChange={(e) => setTradesPerDay(e.target.value)}
              className="w-full p-2 rounded bg-gray-800 text-white border border-gray-700"
              placeholder="Ej: 3"
            />
          </div>

          <button
            type="submit"
            className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded transition"
          >
            Continuar
          </button>
        </form>
      </div>
    </div>
  );
}
