import React, { useState, useEffect } from "react";

import Fondeo from "./Fondeo";
import FondeoSetup from "./FondeoSetup";
import ProgresoMental from "./Progreso";
import RegistroDiario from "./RegistroDiario";


export default function Dashboard() {
  const [screen, setScreen] = useState("dashboard");
  const [config, setConfig] = useState(null);

  const frases = [
    "Bienvenido, te ayudaremos a mejorar tu psicología de trading para alcanzar la rentabilidad.",
    "La clave del éxito es la consistencia y la disciplina.",
    "Una mente fuerte hace un trader fuerte.",
    "El camino a la rentabilidad es mental, no solo técnico.",
    "El mejor trade es el que respeta tu plan.",
  ];
  const [fraseIndex, setFraseIndex] = useState(0);

  useEffect(() => {
    const intervalo = setInterval(() => {
      setFraseIndex((prev) => (prev + 1) % frases.length);
    }, 15000);
    return () => clearInterval(intervalo);
  }, []);

  // Navegación entre módulos
  if (screen === "emociones") return <Emotions onBack={() => setScreen("dashboard")} />;
  if (screen === "registro") return <RegistroDiario onBack={() => setScreen("dashboard")} />;
  if (screen === "progreso") return <ProgresoMental onBack={() => setScreen("dashboard")} />;

  // Configuración previa del módulo Fondeo
  if (screen === "fondeoSetup") {
    return <FondeoSetup onSetupComplete={(data) => {
      setConfig(data);
      setScreen("fondeo");
    }} onBack={() => setScreen("dashboard")} />;
  }

  // Módulo Fondeo con configuración pasada
  if (screen === "fondeo") {
    return <Fondeo config={config} onBack={() => setScreen("dashboard")} />;
  }

  return (
    <div className="min-h-screen bg-black text-white p-6">
      <div className="max-w-5xl mx-auto">
        <h1 className="text-4xl font-bold mb-2 text-indigo-400">📊 MentalTrading Dashboard</h1>
        <p className="text-lg mb-8 text-gray-300">{frases[fraseIndex]}</p>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          <Card title="🧪 Prueba de Fondeo" onClick={() => setScreen("fondeoSetup")} />
          <Card title="📝 Registro Diario" onClick={() => setScreen("registro")} />
          <Card title="🧠 Progreso Mental" onClick={() => setScreen("progreso")} />
          <Card title="🎭 Registro de Emociones" onClick={() => setScreen("emociones")} />
        </div>
      </div>
    </div>
  );
}

function Card({ title, onClick }) {
  return (
    <div
      onClick={onClick}
      className="bg-gray-900 hover:bg-gray-800 transition rounded-xl p-5 cursor-pointer shadow-lg border border-gray-700"
    >
      <h2 className="text-xl font-semibold text-white">{title}</h2>
      <p className="text-sm text-gray-400 mt-2">Ir al módulo</p>
    </div>
  );
}
