import React, { useState, useEffect } from "react";
import PreMarket from "./PreMarket";
import Emotions from "./Emotions";
import Fondeo from "./Fondeo";

import { ArrowRight } from "lucide-react";

const frases = [
  "La disciplina vence al impulso. Opera tu plan.",
  "No se trata de operar mÃ¡s, sino de operar mejor.",
  "Cada dÃ­a es una oportunidad para ser mÃ¡s consistente.",
  "La clave no es ganar siempre, es perder bien.",
  "SÃ© el trader que esperas ser, hoy mismo.",
  "Controla tus emociones, no tus resultados.",
  "La paciencia es tambiÃ©n una operaciÃ³n.",
];

export default function Dashboard() {
  const [screen, setScreen] = useState("dashboard");
  const [frase, setFrase] = useState("");
  const [showFrase, setShowFrase] = useState(true);

  useEffect(() => {
    const cambiarFrase = () => {
      setShowFrase(false);
      setTimeout(() => {
        const random = frases[Math.floor(Math.random() * frases.length)];
        setFrase(random);
        setShowFrase(true);
      }, 500); // tiempo para el fade-out
    };

    cambiarFrase(); // inicial
    const intervalo = setInterval(cambiarFrase, 15000);

    return () => clearInterval(intervalo);
  }, []);
  if (screen === "fondeo") return <Fondeo onBack={() => setScreen("dashboard")} />;

  if (screen === "premarket")
    return <PreMarket onBack={() => setScreen("dashboard")} />;
  if (screen === "emociones")
    return <Emotions onBack={() => setScreen("dashboard")} />;

  return (
    <div
      className="min-h-screen bg-cover bg-center relative"
      style={{
        backgroundImage:
          "url('https://images.unsplash.com/photo-1581093588401-3d5f7f7d82d7?auto=format&fit=crop&w=1600&q=80')",
      }}
    >
      <div className="absolute inset-0 bg-indigo-900 bg-opacity-70"></div>
      <div className="relative z-10 p-6">
        <div className="max-w-6xl mx-auto text-white">
          <h1 className="text-4xl font-bold mb-3 text-center">
            ğŸ§  MentaLTrade
          </h1>

          <p
            className={`text-center text-lg font-medium mb-10 italic transition-opacity duration-500 ease-in-out ${
              showFrase ? "opacity-100" : "opacity-0"
            }`}
          >
            {frase}
          </p>

          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            <Card title="ğŸ§˜ Rutina Pre-Mercado" onClick={() => setScreen("premarket")} />
            <Card title="ğŸ˜Œ Registro de Emociones" onClick={() => setScreen("emociones")} />
            <Card title="ğŸ““ Diario de Trading" onClick={() => alert("PrÃ³ximamente")} />
            <Card title="ğŸ“ˆ Progreso Mental" onClick={() => alert("PrÃ³ximamente")} />
            <Card title="ğŸ’¡ InspiraciÃ³n Diaria" onClick={() => alert("PrÃ³ximamente")} />
            <Card title="Prueba de Fondeo" onClick={() => setScreen("fondeo")} />

          </div>
        </div>
      </div>
    </div>
  );
}

function Card({ title, onClick }) {
  return (
    <div
      onClick={onClick}
      className="bg-white/90 hover:bg-white transition-all rounded-2xl p-6 cursor-pointer shadow-md flex flex-col justify-between h-full"
    >
      <h2 className="text-xl font-semibold text-indigo-800 mb-2">{title}</h2>
      <div className="flex items-center text-sm text-indigo-600 font-medium">
        Ir a mÃ³dulo <ArrowRight className="ml-1 w-4 h-4" />
      </div>
    </div>
  );
}
