import React, { useEffect, useState } from "react";
import FondeoSetup from "./FondeoSetup";

export default function Fondeo({ onBack }) {
  const [setupDone, setSetupDone] = useState(false);
  const [settings, setSettings] = useState(null);

  useEffect(() => {
    const saved = localStorage.getItem("fondeoSettings");
    if (saved) {
      setSettings(JSON.parse(saved));
      setSetupDone(true);
    }
  }, []);

  if (!setupDone) {
    return <FondeoSetup onSetupComplete={() => {
      const saved = localStorage.getItem("fondeoSettings");
      setSettings(JSON.parse(saved));
      setSetupDone(true);
    }} />;
  }

  const riesgoUSD = (settings.capital * (settings.riesgo / 100)).toFixed(2);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-indigo-200 p-6">
      <button
        onClick={onBack}
        className="mb-4 text-indigo-600 hover:underline"
      >
        ← Volver al Dashboard
      </button>

      <h1 className="text-2xl font-bold text-indigo-800 mb-4">
        Módulo: Prueba de Fondeo
      </h1>

      <div className="bg-white rounded-lg shadow p-4 max-w-xl">
        <p><strong>Capital:</strong> ${settings.capital}</p>
        <p><strong>Riesgo por operación:</strong> {settings.riesgo}%</p>
        <p><strong>Máximo trades diarios:</strong> {settings.maxTrades}</p>
        <p><strong>Riesgo máximo por trade:</strong> ${riesgoUSD}</p>
      </div>

      <p className="mt-6 text-gray-700 italic">
        Próximo paso: agregar entrada de trades, emociones y cálculo de progreso.
      </p>
    </div>
  );
}
