import React, { useState } from "react";

export default function FondeoSetup({ onContinue, onBack }) {
  const [balance, setBalance] = useState("");
  const [risk, setRisk] = useState("");
  const [maxTrades, setMaxTrades] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const b = parseFloat(balance);
    const r = parseFloat(risk);
    const m = parseInt(maxTrades);
    if (isNaN(b) || isNaN(r) || isNaN(m) || b <= 0 || r <= 0 || m <= 0) {
      alert("Por favor, ingresa valores v√°lidos.");
      return;
    }
    onContinue({ balance: b, risk: r, maxTrades: m });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-indigo-200 p-6">
      <div className="max-w-md mx-auto bg-white p-6 rounded-xl shadow-md">
        <h2 className="text-2xl font-bold text-indigo-700 mb-4">
          üß™ Configura tu prueba de fondeo
        </h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Balance inicial de la cuenta ($)
            </label>
            <input
              type="number"
              value={balance}
              onChange={(e) => setBalance(e.target.value)}
              className="w-full mt-1 p-2 border rounded-md"
              placeholder="Ej: 100000"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">
              % de riesgo por operaci√≥n
            </label>
            <input
              type="number"
              value={risk}
              onChange={(e) => setRisk(e.target.value)}
              className="w-full mt-1 p-2 border rounded-md"
              placeholder="Ej: 1"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">
              M√°ximo de trades por d√≠a
            </label>
            <input
              type="number"
              value={maxTrades}
              onChange={(e) => setMaxTrades(e.target.value)}
              className="w-full mt-1 p-2 border rounded-md"
              placeholder="Ej: 5"
              required
            />
          </div>

          <div className="flex justify-between pt-4">
            <button
              type="button"
              onClick={onBack}
              className="text-sm text-indigo-600 hover:underline"
            >
              ‚Üê Volver al Dashboard
            </button>
            <button
              type="submit"
              className="bg-indigo-600 text-white px-4 py-2 rounded-md"
            >
              Continuar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
