import React, { useState } from 'react';

export default function PreMarket() {
  const [checked, setChecked] = useState([false, false, false]);
  const [startedBreath, setStartedBreath] = useState(false);

  const checklist = [
    "Dorm√≠ bien y me siento en calma",
    "Acepto que hoy puedo ganar o perder",
    "Me comprometo a seguir mi plan, no mis emociones"
  ];

  const toggleCheck = (index) => {
    const newChecked = [...checked];
    newChecked[index] = !newChecked[index];
    setChecked(newChecked);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-100 to-teal-200 p-6">
      <div className="max-w-2xl mx-auto bg-white p-6 rounded-2xl shadow-xl">
        <button onClick={onBack} className="text-red-600 hover:text-red-800 mb-4 text-sm">
          ‚Üê Volver al Dashboard
        </button>
        <h1 className="text-2xl font-bold text-teal-700 mb-4">üßò Rutina Pre-Mercado</h1>

        <h2 className="text-lg font-semibold mb-2">‚úÖ Checklist Mental</h2>
        <ul className="mb-6">
          {checklist.map((item, index) => (
            <li key={index} className="flex items-center mb-2">
              <input
                type="checkbox"
                className="mr-2 accent-teal-600"
                checked={checked[index]}
                onChange={() => toggleCheck(index)}
              />
              <span>{item}</span>
            </li>
          ))}
        </ul>

        <div className="mb-6">
          <h2 className="text-lg font-semibold mb-2">ü´Å Respiraci√≥n Guiada</h2>
          {!startedBreath ? (
            <button
              onClick={() => setStartedBreath(true)}
              className="bg-teal-600 text-white px-4 py-2 rounded-lg shadow"
            >
              Comenzar respiraci√≥n
            </button>
          ) : (
            <p className="italic text-teal-800">Inhala... Exhala... (hazlo 3 veces lento)</p>
          )}
        </div>

        <div className="mb-6 bg-teal-50 border-l-4 border-teal-400 p-4 rounded">
          <p className="text-teal-800 italic">üí≠ Mantra del d√≠a: ‚ÄúHoy opero con claridad, no con ego.‚Äù</p>
        </div>

        <button className="bg-green-600 text-white px-4 py-2 rounded-lg w-full shadow hover:bg-green-700 transition">
          ‚úÖ Estoy listo para operar
        </button>
      </div>
    </div>
  );
}
