import React, { useState } from "react";

export default function PreMarket({ onBack }) {
  const [checklist, setChecklist] = useState({
    dormiBien: false,
    tengoPlan: false,
    estoyEnfocado: false,
  });

  const [mantra, setMantra] = useState("");

  const handleToggle = (key) => {
    setChecklist({ ...checklist, [key]: !checklist[key] });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Rutina registrada:", checklist, mantra);
    alert("‚úÖ Rutina Pre-Mercado guardada");
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-green-100 to-emerald-200 p-6">
      <div className="max-w-2xl mx-auto bg-white rounded-xl shadow-md p-6">
        <button onClick={onBack} className="text-green-600 hover:text-green-800 mb-4 text-sm">
          ‚Üê Volver al Dashboard
        </button>

        <h2 className="text-2xl font-bold text-emerald-700 mb-4">üåÖ Rutina Pre-Mercado</h2>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <Checkbox
              label="¬øDorm√≠ al menos 6-8 horas?"
              checked={checklist.dormiBien}
              onChange={() => handleToggle("dormiBien")}
            />
            <Checkbox
              label="¬øTengo claro mi plan de trading?"
              checked={checklist.tengoPlan}
              onChange={() => handleToggle("tengoPlan")}
            />
            <Checkbox
              label="¬øEstoy emocionalmente neutral / enfocado?"
              checked={checklist.estoyEnfocado}
              onChange={() => handleToggle("estoyEnfocado")}
            />
          </div>

          <div>
            <label className="block font-medium text-gray-700 mb-1">Mantra mental del d√≠a:</label>
            <input
              type="text"
              className="w-full border rounded p-2"
              placeholder="Ej: Opero bien, no por ganar"
              value={mantra}
              onChange={(e) => setMantra(e.target.value)}
            />
          </div>

          <button
            type="submit"
            className="bg-emerald-500 text-white px-4 py-2 rounded hover:bg-emerald-600 transition"
          >
            Guardar rutina
          </button>
        </form>
      </div>
    </div>
  );
}

function Checkbox({ label, checked, onChange }) {
  return (
    <label className="flex items-center space-x-2 text-gray-800">
      <input type="checkbox" checked={checked} onChange={onChange} className="w-4 h-4" />
      <span>{label}</span>
    </label>
  );
}
