import React, { useState, useEffect } from "react";
import { ArrowLeft, Trophy, BarChart3, Plus, Trash2 } from "lucide-react";

export default function PruebaDeFondeo({ onBack }) {
  const [setup, setSetup] = useState(null);
  const [trades, setTrades] = useState([]);
  const [montoTrade, setMontoTrade] = useState("");
  const [comentario, setComentario] = useState("");

  // Cargar datos del localStorage
  useEffect(() => {
    const data = JSON.parse(localStorage.getItem("fondeoData"));
    if (data?.setup) setSetup(data.setup);
    if (data?.trades) setTrades(data.trades);
  }, []);

  // Guardar en localStorage cuando cambie setup o trades
  useEffect(() => {
    localStorage.setItem("fondeoData", JSON.stringify({ setup, trades }));
  }, [setup, trades]);

  const handleSetup = (e) => {
    e.preventDefault();
    const monto = parseFloat(e.target.monto.value);
    const riesgo = parseFloat(e.target.riesgo.value);
    const meta = monto * 0.1;
    const perdida = monto * (riesgo / 100);
    setSetup({ monto, riesgo, meta, perdida });
  };

  const agregarTrade = () => {
    const monto = parseFloat(montoTrade);
    if (!isNaN(monto)) {
      const nuevo = {
        id: Date.now(),
        monto,
        comentario,
        fecha: new Date().toLocaleString("es-AR"),
      };
      setTrades([nuevo, ...trades]);
      setMontoTrade("");
      setComentario("");
    }
  };

  const borrarTrade = (id) => {
    setTrades(trades.filter((t) => t.id !== id));
  };

  const total = trades.reduce((acc, t) => acc + t.monto, 0);

  if (!setup) {
    return (
      <div className="p-6 text-white max-w-xl mx-auto">
        <button onClick={onBack} className="mb-6 px-4 py-2 bg-blue-600 hover:bg-blue-700 transition rounded flex items-center gap-2">
          <ArrowLeft className="w-4 h-4" /> Volver al Dashboard
        </button>
        <h2 className="text-2xl font-bold mb-4">Configurar Prueba de Fondeo</h2>
        <form onSubmit={handleSetup} className="space-y-4">
          <div>
            <label className="block text-sm mb-1">Monto de la cuenta (USD)</label>
            <input name="monto" type="number" step="0.01" required className="w-full px-4 py-2 bg-slate-800 border border-slate-600 rounded text-white" />
          </div>
          <div>
            <label className="block text-sm mb-1">% Riesgo mÃ¡ximo permitido</label>
            <input name="riesgo" type="number" step="0.1" required className="w-full px-4 py-2 bg-slate-800 border border-slate-600 rounded text-white" />
          </div>
          <button type="submit" className="bg-green-600 px-4 py-2 rounded text-white hover:bg-green-700">Guardar configuraciÃ³n</button>
        </form>
      </div>
    );
  }

  return (
    <div className="p-6 text-white max-w-5xl mx-auto">
      <button onClick={onBack} className="mb-6 px-4 py-2 bg-blue-600 hover:bg-blue-700 transition rounded flex items-center gap-2">
        <ArrowLeft className="w-4 h-4" /> Volver al Dashboard
      </button>

      <h2 className="text-3xl font-bold mb-6 text-blue-300">Prueba de Fondeo</h2>

      {/* Resumen */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
        <ResumenCard icon={Trophy} label="Meta de Ganancia" valor={`$${setup.meta.toFixed(2)}`} />
        <ResumenCard icon={BarChart3} label="PÃ©rdida MÃ¡xima" valor={`$${setup.perdida.toFixed(2)}`} />
        <ResumenCard icon={Trophy} label="Resultado Actual" valor={`$${total.toFixed(2)}`} />
      </div>

      {/* Estado actual */}
      <div className={`mb-8 p-4 rounded-xl ${total >= 0 ? "bg-green-500/20" : "bg-red-500/20"}`}>
        <p className="text-lg font-semibold">
          {total >= 0 ? "Vas en PROFIT ðŸ“ˆ" : "EstÃ¡s en PÃ‰RDIDA ðŸ“‰"}
        </p>
      </div>

      {/* Registro de trade */}
      <div className="bg-slate-800/40 border border-slate-600/30 rounded-2xl p-6 mb-8">
        <h3 className="text-xl font-bold mb-4">Agregar Trade</h3>
        <div className="flex flex-col md:flex-row items-center gap-4">
          <input
            type="number"
            placeholder="Monto ($)"
            value={montoTrade}
            onChange={(e) => setMontoTrade(e.target.value)}
            className="w-full md:w-40 px-4 py-2 bg-slate-800 border border-slate-600 rounded text-white"
          />
          <input
            type="text"
            placeholder="Comentario (opcional)"
            value={comentario}
            onChange={(e) => setComentario(e.target.value)}
            className="w-full px-4 py-2 bg-slate-800 border border-slate-600 rounded text-white"
          />
          <button
            onClick={agregarTrade}
            className="bg-emerald-600 hover:bg-emerald-700 px-4 py-2 rounded text-white flex items-center gap-2"
          >
            <Plus className="w-4 h-4" /> Agregar
          </button>
        </div>
      </div>

      {/* Historial */}
      <div>
        <h3 className="text-xl font-bold mb-4">Historial de Trades</h3>
        {trades.length === 0 ? (
          <p className="text-slate-400">AÃºn no hay registros.</p>
        ) : (
          <ul className="space-y-3">
            {trades.map((t) => (
              <li
                key={t.id}
                className="bg-slate-700/40 border border-slate-600/20 rounded-xl px-4 py-3 flex justify-between items-center"
              >
                <div>
                  <p className="font-mono font-bold text-white">
                    {t.monto >= 0 ? `+ $${t.monto}` : `- $${Math.abs(t.monto)}`}
                  </p>
                  <p className="text-slate-400 text-sm">{t.fecha}</p>
                  {t.comentario && <p className="text-sm text-slate-300 italic mt-1">"{t.comentario}"</p>}
                </div>
                <button onClick={() => borrarTrade(t.id)} className="text-red-400 hover:text-red-600">
                  <Trash2 className="w-5 h-5" />
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

function ResumenCard({ icon: Icon, label, valor }) {
  return (
    <div className="bg-slate-800/40 border border-slate-600/30 rounded-xl p-6 flex items-center gap-4">
      <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-cyan-500 rounded-xl flex items-center justify-center">
        <Icon className="w-6 h-6 text-white" />
      </div>
      <div>
        <p className="text-sm text-slate-400">{label}</p>
        <p className="text-xl font-bold text-white">{valor}</p>
      </div>
    </div>
  );
}
