"use client"

import React, { useState, useEffect } from "react"
import {
  ArrowLeft,
  BookOpen,
  Calendar,
  TrendingUp,
  Brain,
  Target,
  Plus,
  Star,
  CheckCircle,
  AlertCircle,
  Smile,
  Frown,
  Meh,
  BarChart3,
  Flame,
  Eye,
  Sun,
  Moon,
  Activity,
  PenTool,
  X,
  ChevronDown,
  ChevronUp,
} from "lucide-react"

export default function RegistroDiario({ onBack }) {
  const [currentTime, setCurrentTime] = useState(new Date())
  const [activeTab, setActiveTab] = useState("hoy")
  const [showNewEntry, setShowNewEntry] = useState(false)
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split("T")[0])

  const [entradas, setEntradas] = useState([
    {
      id: 1,
      fecha: new Date().toISOString().split("T")[0],
      sesion: "mañana",
      emocionPre: "confident",
      emocionPost: "satisfied",
      objetivos: ["Mantener disciplina", "Máximo 3 trades"],
      logros: ["Respete stop loss", "No hice overtrading"],
      reflexion:
        "Día excelente. Mantuve la calma durante la volatilidad del EUR/USD. Mi estrategia funcionó perfectamente.",
      aprendizajes: "La paciencia es clave en mercados laterales",
      trades: 2,
      resultado: 150,
      estrellas: 5,
      habitos: {
        meditacion: true,
        ejercicio: true,
        lectura: false,
        descanso: true,
      },
    },
    {
      id: 2,
      fecha: new Date(Date.now() - 86400000).toISOString().split("T")[0],
      sesion: "tarde",
      emocionPre: "anxious",
      emocionPost: "frustrated",
      objetivos: ["Recuperar pérdidas de ayer"],
      logros: ["Corte pérdidas rápido"],
      reflexion: "Día difícil. Entré con revenge trading después de las pérdidas. Necesito trabajar en mi mentalidad.",
      aprendizajes: "No debo tradear cuando estoy emocionalmente comprometido",
      trades: 5,
      resultado: -80,
      estrellas: 2,
      habitos: {
        meditacion: false,
        ejercicio: false,
        lectura: true,
        descanso: false,
      },
    },
  ])

  const [nuevaEntrada, setNuevaEntrada] = useState({
    fecha: new Date().toISOString().split("T")[0],
    sesion: "mañana",
    emocionPre: "neutral",
    emocionPost: "neutral",
    objetivos: [""],
    logros: [""],
    reflexion: "",
    aprendizajes: "",
    trades: 0,
    resultado: 0,
    estrellas: 3,
    habitos: {
      meditacion: false,
      ejercicio: false,
      lectura: false,
      descanso: false,
    },
  })

  const emociones = {
    excited: { icon: Star, label: "Emocionado", color: "text-yellow-400", bg: "bg-yellow-500/20" },
    confident: { icon: Smile, label: "Confiado", color: "text-green-400", bg: "bg-green-500/20" },
    neutral: { icon: Meh, label: "Neutral", color: "text-blue-400", bg: "bg-blue-500/20" },
    anxious: { icon: AlertCircle, label: "Ansioso", color: "text-orange-400", bg: "bg-orange-500/20" },
    frustrated: { icon: Frown, label: "Frustrado", color: "text-red-400", bg: "bg-red-500/20" },
    satisfied: { icon: CheckCircle, label: "Satisfecho", color: "text-emerald-400", bg: "bg-emerald-500/20" },
  }

  const habitos = {
    meditacion: { icon: Brain, label: "Meditación", color: "text-purple-400" },
    ejercicio: { icon: Activity, label: "Ejercicio", color: "text-green-400" },
    lectura: { icon: BookOpen, label: "Lectura", color: "text-blue-400" },
    descanso: { icon: Moon, label: "Buen Descanso", color: "text-indigo-400" },
  }

  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date()), 1000)
    return () => clearInterval(timer)
  }, [])

  const handleSaveEntry = () => {
    const entrada = {
      ...nuevaEntrada,
      id: Date.now(),
      objetivos: nuevaEntrada.objetivos.filter((obj) => obj.trim() !== ""),
      logros: nuevaEntrada.logros.filter((logro) => logro.trim() !== ""),
    }

    setEntradas((prev) => [entrada, ...prev])
    setNuevaEntrada({
      fecha: new Date().toISOString().split("T")[0],
      sesion: "mañana",
      emocionPre: "neutral",
      emocionPost: "neutral",
      objetivos: [""],
      logros: [""],
      reflexion: "",
      aprendizajes: "",
      trades: 0,
      resultado: 0,
      estrellas: 3,
      habitos: {
        meditacion: false,
        ejercicio: false,
        lectura: false,
        descanso: false,
      },
    })
    setShowNewEntry(false)
  }

  const getEstadisticas = () => {
    const ultimaSemana = entradas.filter((entrada) => {
      const fechaEntrada = new Date(entrada.fecha)
      const haceUnaSemana = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000)
      return fechaEntrada >= haceUnaSemana
    })

    const promedioEstrellas =
      ultimaSemana.reduce((acc, entrada) => acc + entrada.estrellas, 0) / ultimaSemana.length || 0
    const totalTrades = ultimaSemana.reduce((acc, entrada) => acc + entrada.trades, 0)
    const resultadoTotal = ultimaSemana.reduce((acc, entrada) => acc + entrada.resultado, 0)
    const habitosCompletados = ultimaSemana.reduce((acc, entrada) => {
      return acc + Object.values(entrada.habitos).filter(Boolean).length
    }, 0)

    return {
      promedioEstrellas: promedioEstrellas.toFixed(1),
      totalTrades,
      resultadoTotal,
      habitosCompletados,
      diasRegistrados: ultimaSemana.length,
    }
  }

  const stats = getEstadisticas()

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-950 via-slate-900 to-indigo-950 text-white relative overflow-hidden">
      {/* Elementos de fondo */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-purple-500/10 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-blue-500/10 rounded-full blur-3xl animate-pulse delay-1000"></div>
      </div>

      <div className="absolute inset-0 bg-[radial-gradient(circle_at_1px_1px,rgba(255,255,255,0.05)_1px,transparent_0)] bg-[size:20px_20px] pointer-events-none"></div>

      <div className="relative z-10 max-w-7xl mx-auto p-6">
        {/* Header */}
        <header className="mb-8">
          <div className="flex items-center justify-between mb-6">
            <button
              onClick={onBack}
              className="flex items-center gap-2 px-4 py-2 bg-slate-800/50 hover:bg-slate-700/50 transition-all duration-300 rounded-xl border border-slate-600/30 backdrop-blur-sm"
            >
              <ArrowLeft className="w-4 h-4" />
              <span>Volver al Dashboard</span>
            </button>

            <div className="flex items-center gap-4">
              <button
                onClick={() => setShowNewEntry(true)}
                className="flex items-center gap-2 px-4 py-2 bg-gradient-to-r from-purple-600 to-pink-600 hover:brightness-110 rounded-xl transition-all duration-300"
              >
                <Plus className="w-4 h-4" />
                <span>Nueva Entrada</span>
              </button>
              <div className="text-right">
                <p className="text-lg font-mono font-bold text-purple-400">{currentTime.toLocaleTimeString("es-AR")}</p>
                <p className="text-xs text-slate-400">Tiempo Real</p>
              </div>
            </div>
          </div>

          <div className="mb-6">
            <h1 className="text-4xl font-bold mb-2 bg-gradient-to-r from-purple-400 via-pink-400 to-blue-400 bg-clip-text text-transparent">
              Registro Diario
            </h1>
            <p className="text-slate-400 max-w-3xl">
              Tu journal personal de trading. Registra emociones, reflexiones y patrones para mejorar tu psicología de
              trading.
            </p>
          </div>

          {/* Estadísticas rápidas */}
          <div className="grid grid-cols-1 md:grid-cols-5 gap-4 mb-6">
            <StatCard
              icon={Star}
              label="Promedio Semanal"
              value={`${stats.promedioEstrellas}★`}
              color="from-yellow-500 to-orange-500"
            />
            <StatCard
              icon={BarChart3}
              label="Trades Semana"
              value={stats.totalTrades}
              color="from-blue-500 to-cyan-500"
            />
            <StatCard
              icon={TrendingUp}
              label="P&L Semanal"
              value={`$${stats.resultadoTotal}`}
              color={stats.resultadoTotal >= 0 ? "from-green-500 to-emerald-500" : "from-red-500 to-pink-500"}
            />
            <StatCard
              icon={CheckCircle}
              label="Hábitos Completados"
              value={stats.habitosCompletados}
              color="from-purple-500 to-pink-500"
            />
            <StatCard
              icon={Calendar}
              label="Días Registrados"
              value={stats.diasRegistrados}
              color="from-indigo-500 to-purple-500"
            />
          </div>
        </header>

        {/* Navegación por tabs */}
        <div className="mb-8">
          <div className="flex space-x-1 bg-slate-800/30 p-1 rounded-xl border border-slate-600/30 backdrop-blur-sm">
            {[
              { id: "hoy", label: "Hoy", icon: Sun },
              { id: "historial", label: "Historial", icon: Calendar },
              { id: "patrones", label: "Patrones", icon: BarChart3 },
              { id: "habitos", label: "Hábitos", icon: Target },
            ].map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center gap-2 px-4 py-2 rounded-lg transition-all duration-300 ${
                  activeTab === tab.id
                    ? "bg-purple-600 text-white shadow-lg"
                    : "text-slate-400 hover:text-white hover:bg-slate-700/50"
                }`}
              >
                <tab.icon className="w-4 h-4" />
                <span className="font-medium">{tab.label}</span>
              </button>
            ))}
          </div>
        </div>

        {/* Contenido según tab */}
        {activeTab === "hoy" && (
          <TodayTab
            entradas={entradas.filter((e) => e.fecha === new Date().toISOString().split("T")[0])}
            emociones={emociones}
            habitos={habitos}
            onNewEntry={() => setShowNewEntry(true)}
          />
        )}

        {activeTab === "historial" && (
          <HistorialTab
            entradas={entradas}
            emociones={emociones}
            habitos={habitos}
            selectedDate={selectedDate}
            setSelectedDate={setSelectedDate}
          />
        )}

        {activeTab === "patrones" && <PatronesTab entradas={entradas} emociones={emociones} />}

        {activeTab === "habitos" && <HabitosTab entradas={entradas} habitos={habitos} />}
      </div>

      {/* Modal Nueva Entrada */}
      {showNewEntry && (
        <NuevaEntradaModal
          entrada={nuevaEntrada}
          setEntrada={setNuevaEntrada}
          emociones={emociones}
          habitos={habitos}
          onClose={() => setShowNewEntry(false)}
          onSave={handleSaveEntry}
        />
      )}
    </div>
  )
}

// Componente StatCard
function StatCard({ icon: Icon, label, value, color }) {
  return (
    <div className="bg-slate-800/50 backdrop-blur-sm rounded-xl p-4 border border-slate-600/30 hover:border-slate-500/50 transition-all duration-300">
      <div className="flex items-center gap-3 mb-2">
        <div className={`w-8 h-8 bg-gradient-to-r ${color} rounded-lg flex items-center justify-center`}>
          <Icon className="w-4 h-4 text-white" />
        </div>
        <span className="text-sm text-slate-400">{label}</span>
      </div>
      <p className="text-xl font-bold text-white">{value}</p>
    </div>
  )
}

// Tab Hoy
function TodayTab({ entradas, emociones, habitos, onNewEntry }) {
  const entradaHoy = entradas[0]

  if (!entradaHoy) {
    return (
      <div className="text-center py-12">
        <div className="w-24 h-24 bg-gradient-to-r from-purple-500/20 to-pink-500/20 rounded-full flex items-center justify-center mx-auto mb-6">
          <PenTool className="w-12 h-12 text-purple-400" />
        </div>
        <h3 className="text-2xl font-bold text-white mb-4">¡Comienza tu día!</h3>
        <p className="text-slate-400 mb-6 max-w-md mx-auto">
          Aún no has registrado tu sesión de hoy. Crear una entrada te ayudará a mantener el foco y la disciplina.
        </p>
        <button
          onClick={onNewEntry}
          className="px-6 py-3 bg-gradient-to-r from-purple-600 to-pink-600 hover:brightness-110 rounded-xl transition-all duration-300 font-semibold"
        >
          Crear Primera Entrada
        </button>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      <EntradaCard entrada={entradaHoy} emociones={emociones} habitos={habitos} />
    </div>
  )
}

// Tab Historial
function HistorialTab({ entradas, emociones, habitos, selectedDate, setSelectedDate }) {
  return (
    <div className="space-y-6">
      <div className="flex items-center gap-4 mb-6">
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
          className="px-4 py-2 bg-slate-700/50 border border-slate-600 rounded-lg text-white focus:border-purple-500 focus:outline-none"
        />
        <div className="flex items-center gap-2 text-slate-400">
          <Calendar className="w-4 h-4" />
          <span className="text-sm">Total de entradas: {entradas.length}</span>
        </div>
      </div>

      <div className="space-y-4">
        {entradas.map((entrada) => (
          <EntradaCard key={entrada.id} entrada={entrada} emociones={emociones} habitos={habitos} compact />
        ))}
      </div>
    </div>
  )
}

// Tab Patrones
function PatronesTab({ entradas, emociones }) {
  const analisisEmocional = () => {
    const distribucion = {}
    entradas.forEach((entrada) => {
      distribucion[entrada.emocionPre] = (distribucion[entrada.emocionPre] || 0) + 1
    })
    return distribucion
  }

  const correlacionResultados = () => {
    const positivos = entradas.filter((e) => e.resultado > 0)
    const negativos = entradas.filter((e) => e.resultado < 0)

    return {
      emocionesPositivas: positivos.reduce((acc, e) => {
        acc[e.emocionPre] = (acc[e.emocionPre] || 0) + 1
        return acc
      }, {}),
      emocionesNegativas: negativos.reduce((acc, e) => {
        acc[e.emocionPre] = (acc[e.emocionPre] || 0) + 1
        return acc
      }, {}),
    }
  }

  const distribucion = analisisEmocional()
  const correlacion = correlacionResultados()

  return (
    <div className="space-y-6">
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Distribución emocional */}
        <div className="bg-slate-800/30 rounded-2xl p-6 border border-slate-600/30 backdrop-blur-sm">
          <h3 className="text-xl font-bold text-white mb-4 flex items-center gap-2">
            <Brain className="w-5 h-5 text-purple-400" />
            Distribución Emocional
          </h3>
          <div className="space-y-3">
            {Object.entries(distribucion).map(([emocion, count]) => (
              <div key={emocion} className="flex items-center justify-between">
                <div className="flex items-center gap-2">
                  {React.createElement(emociones[emocion].icon, {
                    className: `w-4 h-4 ${emociones[emocion].color}`,
                  })}
                  <span className="text-sm text-white">{emociones[emocion].label}</span>
                </div>
                <div className="flex items-center gap-2">
                  <div className="w-20 bg-slate-700 rounded-full h-2">
                    <div
                      className="h-full bg-purple-500 rounded-full transition-all duration-500"
                      style={{ width: `${(count / entradas.length) * 100}%` }}
                    ></div>
                  </div>
                  <span className="text-sm text-slate-400 w-8">{count}</span>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Correlación emociones-resultados */}
        <div className="bg-slate-800/30 rounded-2xl p-6 border border-slate-600/30 backdrop-blur-sm">
          <h3 className="text-xl font-bold text-white mb-4 flex items-center gap-2">
            <TrendingUp className="w-5 h-5 text-green-400" />
            Emociones vs Resultados
          </h3>
          <div className="space-y-4">
            <div>
              <h4 className="text-sm font-medium text-green-400 mb-2">Trades Exitosos</h4>
              <div className="space-y-2">
                {Object.entries(correlacion.emocionesPositivas).map(([emocion, count]) => (
                  <div key={emocion} className="flex items-center justify-between text-sm">
                    <span className="text-slate-300">{emociones[emocion]?.label}</span>
                    <span className="text-green-400">{count}</span>
                  </div>
                ))}
              </div>
            </div>
            <div>
              <h4 className="text-sm font-medium text-red-400 mb-2">Trades con Pérdidas</h4>
              <div className="space-y-2">
                {Object.entries(correlacion.emocionesNegativas).map(([emocion, count]) => (
                  <div key={emocion} className="flex items-center justify-between text-sm">
                    <span className="text-slate-300">{emociones[emocion]?.label}</span>
                    <span className="text-red-400">{count}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Insights automáticos */}
      <div className="bg-slate-800/30 rounded-2xl p-6 border border-slate-600/30 backdrop-blur-sm">
        <h3 className="text-xl font-bold text-white mb-4 flex items-center gap-2">
          <Eye className="w-5 h-5 text-blue-400" />
          Insights Automáticos
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="bg-slate-700/30 rounded-xl p-4">
            <h4 className="font-semibold text-white mb-2">Patrón Identificado</h4>
            <p className="text-sm text-slate-400">
              Tus mejores resultados ocurren cuando te sientes "confiado" antes de tradear.
            </p>
          </div>
          <div className="bg-slate-700/30 rounded-xl p-4">
            <h4 className="font-semibold text-white mb-2">Recomendación</h4>
            <p className="text-sm text-slate-400">
              Evita tradear cuando te sientes "ansioso" - históricamente genera pérdidas.
            </p>
          </div>
        </div>
      </div>
    </div>
  )
}

// Tab Hábitos
function HabitosTab({ entradas, habitos }) {
  const analisisHabitos = () => {
    const stats = {}
    Object.keys(habitos).forEach((habito) => {
      const completados = entradas.filter((e) => e.habitos[habito]).length
      const porcentaje = (completados / entradas.length) * 100
      stats[habito] = { completados, porcentaje }
    })
    return stats
  }

  const stats = analisisHabitos()

  return (
    <div className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {Object.entries(habitos).map(([key, habito]) => (
          <div key={key} className="bg-slate-800/30 rounded-2xl p-6 border border-slate-600/30 backdrop-blur-sm">
            <div className="flex items-center gap-3 mb-4">
              <div
                className={`w-12 h-12 bg-gradient-to-r from-purple-500 to-pink-500 rounded-xl flex items-center justify-center`}
              >
                <habito.icon className="w-6 h-6 text-white" />
              </div>
              <div>
                <h4 className="font-semibold text-white">{habito.label}</h4>
                <p className="text-xs text-slate-400">Últimos 30 días</p>
              </div>
            </div>
            <div className="space-y-2">
              <div className="flex justify-between items-center">
                <span className="text-sm text-slate-400">Completado</span>
                <span className="text-sm font-medium text-white">{stats[key]?.porcentaje.toFixed(0)}%</span>
              </div>
              <div className="w-full bg-slate-700 rounded-full h-2">
                <div
                  className="h-full bg-gradient-to-r from-purple-500 to-pink-500 rounded-full transition-all duration-1000"
                  style={{ width: `${stats[key]?.porcentaje || 0}%` }}
                ></div>
              </div>
              <p className="text-xs text-slate-500">
                {stats[key]?.completados} de {entradas.length} días
              </p>
            </div>
          </div>
        ))}
      </div>

      {/* Racha actual */}
      <div className="bg-slate-800/30 rounded-2xl p-6 border border-slate-600/30 backdrop-blur-sm">
        <h3 className="text-xl font-bold text-white mb-4 flex items-center gap-2">
          <Flame className="w-5 h-5 text-orange-400" />
          Rachas Actuales
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          {Object.entries(habitos).map(([key, habito]) => (
            <div key={key} className="text-center">
              <div className="w-16 h-16 bg-gradient-to-r from-orange-500 to-red-500 rounded-full flex items-center justify-center mx-auto mb-2">
                <span className="text-2xl font-bold text-white">3</span>
              </div>
              <p className="text-sm text-white font-medium">{habito.label}</p>
              <p className="text-xs text-slate-400">días consecutivos</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  )
}

// Componente EntradaCard
function EntradaCard({ entrada, emociones, habitos, compact = false }) {
  const [expanded, setExpanded] = useState(!compact)

  return (
    <div className="bg-slate-800/30 rounded-2xl border border-slate-600/30 backdrop-blur-sm overflow-hidden">
      <div className="p-6">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-4">
            <div className="text-left">
              <h3 className="text-lg font-bold text-white">{new Date(entrada.fecha).toLocaleDateString("es-AR")}</h3>
              <p className="text-sm text-slate-400 capitalize">Sesión de {entrada.sesion}</p>
            </div>
            <div className="flex items-center gap-2">
              {[...Array(5)].map((_, i) => (
                <Star
                  key={i}
                  className={`w-4 h-4 ${i < entrada.estrellas ? "text-yellow-400 fill-current" : "text-slate-600"}`}
                />
              ))}
            </div>
          </div>
          <div className="flex items-center gap-4">
            <div className="text-right">
              <p className={`text-lg font-bold ${entrada.resultado >= 0 ? "text-green-400" : "text-red-400"}`}>
                {entrada.resultado >= 0 ? "+" : ""}${entrada.resultado}
              </p>
              <p className="text-xs text-slate-400">{entrada.trades} trades</p>
            </div>
            {compact && (
              <button
                onClick={() => setExpanded(!expanded)}
                className="p-2 hover:bg-slate-700/50 rounded-lg transition-colors"
              >
                {expanded ? (
                  <ChevronUp className="w-4 h-4 text-slate-400" />
                ) : (
                  <ChevronDown className="w-4 h-4 text-slate-400" />
                )}
              </button>
            )}
          </div>
        </div>

        {expanded && (
          <div className="space-y-4">
            {/* Emociones */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="flex items-center gap-3">
                <span className="text-sm text-slate-400">Pre-trading:</span>
                <div className={`flex items-center gap-2 px-3 py-1 rounded-lg ${emociones[entrada.emocionPre].bg}`}>
                  {React.createElement(emociones[entrada.emocionPre].icon, {
                    className: `w-4 h-4 ${emociones[entrada.emocionPre].color}`,
                  })}
                  <span className="text-sm text-white">{emociones[entrada.emocionPre].label}</span>
                </div>
              </div>
              <div className="flex items-center gap-3">
                <span className="text-sm text-slate-400">Post-trading:</span>
                <div className={`flex items-center gap-2 px-3 py-1 rounded-lg ${emociones[entrada.emocionPost].bg}`}>
                  {React.createElement(emociones[entrada.emocionPost].icon, {
                    className: `w-4 h-4 ${emociones[entrada.emocionPost].color}`,
                  })}
                  <span className="text-sm text-white">{emociones[entrada.emocionPost].label}</span>
                </div>
              </div>
            </div>

            {/* Objetivos y Logros */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <h4 className="text-sm font-medium text-slate-300 mb-2">Objetivos del Día</h4>
                <ul className="space-y-1">
                  {entrada.objetivos.map((objetivo, index) => (
                    <li key={index} className="flex items-center gap-2 text-sm text-slate-400">
                      <Target className="w-3 h-3 text-blue-400" />
                      {objetivo}
                    </li>
                  ))}
                </ul>
              </div>
              <div>
                <h4 className="text-sm font-medium text-slate-300 mb-2">Logros Alcanzados</h4>
                <ul className="space-y-1">
                  {entrada.logros.map((logro, index) => (
                    <li key={index} className="flex items-center gap-2 text-sm text-slate-400">
                      <CheckCircle className="w-3 h-3 text-green-400" />
                      {logro}
                    </li>
                  ))}
                </ul>
              </div>
            </div>

            {/* Reflexión */}
            <div>
              <h4 className="text-sm font-medium text-slate-300 mb-2">Reflexión del Día</h4>
              <p className="text-sm text-slate-400 bg-slate-700/30 rounded-lg p-3">{entrada.reflexion}</p>
            </div>

            {/* Aprendizajes */}
            <div>
              <h4 className="text-sm font-medium text-slate-300 mb-2">Aprendizajes</h4>
              <p className="text-sm text-slate-400 bg-slate-700/30 rounded-lg p-3">{entrada.aprendizajes}</p>
            </div>

            {/* Hábitos */}
            <div>
              <h4 className="text-sm font-medium text-slate-300 mb-2">Hábitos Completados</h4>
              <div className="flex gap-2">
                {Object.entries(entrada.habitos).map(([key, completado]) => (
                  <div
                    key={key}
                    className={`flex items-center gap-2 px-3 py-1 rounded-lg ${
                      completado ? "bg-green-500/20 text-green-400" : "bg-slate-700/30 text-slate-500"
                    }`}
                  >
                    {React.createElement(habitos[key].icon, { className: "w-3 h-3" })}
                    <span className="text-xs">{habitos[key].label}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  )
}

// Modal Nueva Entrada
function NuevaEntradaModal({ entrada, setEntrada, emociones, habitos, onClose, onSave }) {
  const addObjetivo = () => {
    setEntrada((prev) => ({
      ...prev,
      objetivos: [...prev.objetivos, ""],
    }))
  }

  const addLogro = () => {
    setEntrada((prev) => ({
      ...prev,
      logros: [...prev.logros, ""],
    }))
  }

  const updateObjetivo = (index, value) => {
    setEntrada((prev) => ({
      ...prev,
      objetivos: prev.objetivos.map((obj, i) => (i === index ? value : obj)),
    }))
  }

  const updateLogro = (index, value) => {
    setEntrada((prev) => ({
      ...prev,
      logros: prev.logros.map((logro, i) => (i === index ? value : logro)),
    }))
  }

  const toggleHabito = (habito) => {
    setEntrada((prev) => ({
      ...prev,
      habitos: {
        ...prev.habitos,
        [habito]: !prev.habitos[habito],
      },
    }))
  }

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center p-6 z-50 overflow-y-auto">
      <div className="bg-slate-800/90 backdrop-blur-sm rounded-2xl border border-slate-600/30 max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="sticky top-0 bg-slate-800/90 backdrop-blur-sm p-6 border-b border-slate-600/30">
          <div className="flex items-center justify-between">
            <h3 className="text-2xl font-bold text-white">Nueva Entrada de Journal</h3>
            <button onClick={onClose} className="p-2 hover:bg-slate-700/50 rounded-lg transition-colors">
              <X className="w-5 h-5 text-slate-400" />
            </button>
          </div>
        </div>

        <div className="p-6 space-y-6">
          {/* Fecha y Sesión */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-slate-300 mb-2">Fecha</label>
              <input
                type="date"
                value={entrada.fecha}
                onChange={(e) => setEntrada((prev) => ({ ...prev, fecha: e.target.value }))}
                className="w-full px-3 py-2 bg-slate-700/50 border border-slate-600 rounded-lg text-white focus:border-purple-500 focus:outline-none"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-slate-300 mb-2">Sesión</label>
              <select
                value={entrada.sesion}
                onChange={(e) => setEntrada((prev) => ({ ...prev, sesion: e.target.value }))}
                className="w-full px-3 py-2 bg-slate-700/50 border border-slate-600 rounded-lg text-white focus:border-purple-500 focus:outline-none"
              >
                <option value="mañana">Mañana</option>
                <option value="tarde">Tarde</option>
                <option value="noche">Noche</option>
              </select>
            </div>
          </div>

          {/* Emociones */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-slate-300 mb-2">
                ¿Cómo te sentías antes de tradear?
              </label>
              <div className="grid grid-cols-2 gap-2">
                {Object.entries(emociones).map(([key, emocion]) => (
                  <button
                    key={key}
                    onClick={() => setEntrada((prev) => ({ ...prev, emocionPre: key }))}
                    className={`flex items-center gap-2 p-2 rounded-lg border transition-all duration-300 ${
                      entrada.emocionPre === key
                        ? "border-purple-500 bg-purple-500/20"
                        : "border-slate-600 bg-slate-700/30 hover:border-slate-500"
                    }`}
                  >
                    <emocion.icon className={`w-4 h-4 ${emocion.color}`} />
                    <span className="text-xs text-white">{emocion.label}</span>
                  </button>
                ))}
              </div>
            </div>
            <div>
              <label className="block text-sm font-medium text-slate-300 mb-2">¿Cómo te sientes ahora?</label>
              <div className="grid grid-cols-2 gap-2">
                {Object.entries(emociones).map(([key, emocion]) => (
                  <button
                    key={key}
                    onClick={() => setEntrada((prev) => ({ ...prev, emocionPost: key }))}
                    className={`flex items-center gap-2 p-2 rounded-lg border transition-all duration-300 ${
                      entrada.emocionPost === key
                        ? "border-purple-500 bg-purple-500/20"
                        : "border-slate-600 bg-slate-700/30 hover:border-slate-500"
                    }`}
                  >
                    <emocion.icon className={`w-4 h-4 ${emocion.color}`} />
                    <span className="text-xs text-white">{emocion.label}</span>
                  </button>
                ))}
              </div>
            </div>
          </div>

          {/* Trades y Resultado */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-slate-300 mb-2">Número de Trades</label>
              <input
                type="number"
                min="0"
                value={entrada.trades}
                onChange={(e) => setEntrada((prev) => ({ ...prev, trades: Number.parseInt(e.target.value) || 0 }))}
                className="w-full px-3 py-2 bg-slate-700/50 border border-slate-600 rounded-lg text-white focus:border-purple-500 focus:outline-none"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-slate-300 mb-2">Resultado P&L ($)</label>
              <input
                type="number"
                step="0.01"
                value={entrada.resultado}
                onChange={(e) => setEntrada((prev) => ({ ...prev, resultado: Number.parseFloat(e.target.value) || 0 }))}
                className="w-full px-3 py-2 bg-slate-700/50 border border-slate-600 rounded-lg text-white focus:border-purple-500 focus:outline-none"
              />
            </div>
          </div>

          {/* Calificación del día */}
          <div>
            <label className="block text-sm font-medium text-slate-300 mb-2">Califica tu día (1-5 estrellas)</label>
            <div className="flex gap-1">
              {[1, 2, 3, 4, 5].map((star) => (
                <button key={star} onClick={() => setEntrada((prev) => ({ ...prev, estrellas: star }))} className="p-1">
                  <Star
                    className={`w-6 h-6 ${
                      star <= entrada.estrellas ? "text-yellow-400 fill-current" : "text-slate-600"
                    } hover:text-yellow-300 transition-colors`}
                  />
                </button>
              ))}
            </div>
          </div>

          {/* Objetivos */}
          <div>
            <div className="flex items-center justify-between mb-2">
              <label className="text-sm font-medium text-slate-300">Objetivos del Día</label>
              <button
                onClick={addObjetivo}
                className="flex items-center gap-1 px-2 py-1 bg-purple-600/20 hover:bg-purple-600/30 rounded text-xs text-purple-400 transition-colors"
              >
                <Plus className="w-3 h-3" />
                Agregar
              </button>
            </div>
            <div className="space-y-2">
              {entrada.objetivos.map((objetivo, index) => (
                <input
                  key={index}
                  type="text"
                  placeholder="Ej: Mantener disciplina, máximo 3 trades"
                  value={objetivo}
                  onChange={(e) => updateObjetivo(index, e.target.value)}
                  className="w-full px-3 py-2 bg-slate-700/50 border border-slate-600 rounded-lg text-white focus:border-purple-500 focus:outline-none text-sm"
                />
              ))}
            </div>
          </div>

          {/* Logros */}
          <div>
            <div className="flex items-center justify-between mb-2">
              <label className="text-sm font-medium text-slate-300">Logros Alcanzados</label>
              <button
                onClick={addLogro}
                className="flex items-center gap-1 px-2 py-1 bg-green-600/20 hover:bg-green-600/30 rounded text-xs text-green-400 transition-colors"
              >
                <Plus className="w-3 h-3" />
                Agregar
              </button>
            </div>
            <div className="space-y-2">
              {entrada.logros.map((logro, index) => (
                <input
                  key={index}
                  type="text"
                  placeholder="Ej: Respete mi stop loss, no hice overtrading"
                  value={logro}
                  onChange={(e) => updateLogro(index, e.target.value)}
                  className="w-full px-3 py-2 bg-slate-700/50 border border-slate-600 rounded-lg text-white focus:border-purple-500 focus:outline-none text-sm"
                />
              ))}
            </div>
          </div>

          {/* Reflexión */}
          <div>
            <label className="block text-sm font-medium text-slate-300 mb-2">Reflexión del Día</label>
            <textarea
              placeholder="¿Qué pasó hoy? ¿Cómo te sentiste? ¿Qué funcionó bien?"
              value={entrada.reflexion}
              onChange={(e) => setEntrada((prev) => ({ ...prev, reflexion: e.target.value }))}
              className="w-full px-3 py-2 bg-slate-700/50 border border-slate-600 rounded-lg text-white focus:border-purple-500 focus:outline-none resize-none"
              rows={4}
            />
          </div>

          {/* Aprendizajes */}
          <div>
            <label className="block text-sm font-medium text-slate-300 mb-2">Aprendizajes Clave</label>
            <textarea
              placeholder="¿Qué aprendiste hoy? ¿Qué harías diferente?"
              value={entrada.aprendizajes}
              onChange={(e) => setEntrada((prev) => ({ ...prev, aprendizajes: e.target.value }))}
              className="w-full px-3 py-2 bg-slate-700/50 border border-slate-600 rounded-lg text-white focus:border-purple-500 focus:outline-none resize-none"
              rows={3}
            />
          </div>

          {/* Hábitos */}
          <div>
            <label className="block text-sm font-medium text-slate-300 mb-2">Hábitos Completados Hoy</label>
            <div className="grid grid-cols-2 gap-2">
              {Object.entries(habitos).map(([key, habito]) => (
                <button
                  key={key}
                  onClick={() => toggleHabito(key)}
                  className={`flex items-center gap-2 p-3 rounded-lg border transition-all duration-300 ${
                    entrada.habitos[key]
                      ? "border-green-500 bg-green-500/20 text-green-400"
                      : "border-slate-600 bg-slate-700/30 text-slate-400 hover:border-slate-500"
                  }`}
                >
                  <habito.icon className="w-4 h-4" />
                  <span className="text-sm">{habito.label}</span>
                  {entrada.habitos[key] && <CheckCircle className="w-4 h-4 ml-auto" />}
                </button>
              ))}
            </div>
          </div>
        </div>

        <div className="sticky bottom-0 bg-slate-800/90 backdrop-blur-sm p-6 border-t border-slate-600/30">
          <div className="flex gap-3">
            <button
              onClick={onClose}
              className="flex-1 px-4 py-2 bg-slate-700/50 hover:bg-slate-600/50 rounded-lg transition-colors"
            >
              Cancelar
            </button>
            <button
              onClick={onSave}
              className="flex-1 px-4 py-2 bg-gradient-to-r from-purple-600 to-pink-600 hover:brightness-110 rounded-lg transition-all duration-300 font-semibold"
            >
              Guardar Entrada
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}
