// Nuevo módulo: Prueba de Fondeo integrado al Dashboard original
import React, { useState, useEffect } from "react";
import {
  TrendingUp,
  Brain,
  BookOpen,
  Target,
  BarChart3,
  Zap,
  Star,
  ArrowRight,
  Clock,
  Trophy,
  Activity,
  Eye,
  XCircle,
  CheckCircle
} from "lucide-react";

// Nuevo módulo de Prueba de Fondeo
const PruebaFondeo = ({ onBack }) => {
  return (
    <div className="p-6 text-white">
      <button onClick={onBack} className="mb-6 px-4 py-2 bg-blue-600 text-white rounded">
        ← Volver
      </button>

      <h2 className="text-3xl font-bold mb-2 bg-gradient-to-r from-blue-400 via-purple-400 to-cyan-400 bg-clip-text text-transparent">
        Prueba de Fondeo
      </h2>
      <p className="text-slate-400 mb-8">Evaluación completa de fondeo y métricas clave</p>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <FondeoCard
          icon={TrendingUp}
          label="Objetivo de Ganancia"
          value="$2,000 / $5,000"
          color="from-green-500 to-emerald-500"
        />
        <FondeoCard
          icon={XCircle}
          label="Drawdown Máximo"
          value="-$300 / -$500"
          color="from-red-500 to-pink-500"
        />
        <FondeoCard
          icon={Trophy}
          label="Días Restantes"
          value="12 días"
          color="from-yellow-500 to-orange-500"
        />
      </div>

      <div className="mb-8">
        <h3 className="text-xl font-semibold mb-2 text-white">Progreso General</h3>
        <div className="w-full bg-slate-700 rounded-full h-4 overflow-hidden">
          <div className="bg-gradient-to-r from-blue-500 to-cyan-400 h-full w-[40%] transition-all duration-500"></div>
        </div>
        <p className="text-sm text-slate-400 mt-1">40% completado</p>
      </div>

      <div className="mt-10">
        <button
          onClick={() => alert("Simular avance")}
          className="px-6 py-3 bg-green-600 hover:bg-green-700 text-white rounded-xl font-semibold transition-all duration-300 flex items-center gap-2"
        >
          <CheckCircle className="w-5 h-5" />
          Marcar avance
        </button>
      </div>
    </div>
  );
};

const FondeoCard = ({ icon: Icon, label, value, color }) => (
  <div className="bg-slate-800/50 backdrop-blur-sm rounded-2xl p-6 border border-slate-600/30 hover:border-slate-500/50 transition-all duration-300 group">
    <div className="flex items-center justify-between mb-4">
      <div className={`w-12 h-12 bg-gradient-to-r ${color} rounded-xl flex items-center justify-center`}>
        <Icon className="w-6 h-6 text-white" />
      </div>
    </div>
    <p className="text-xl font-bold text-white mb-1">{value}</p>
    <p className="text-sm text-slate-400">{label}</p>
  </div>
);

// Componentes originales simulados
const ProgresoMental = ({ onBack }) => <div className="p-6"><button onClick={onBack} className="mb-4 px-4 py-2 bg-blue-600 text-white rounded">← Volver</button><h2>Progreso Mental</h2></div>;
const RegistroDiario = ({ onBack }) => <div className="p-6"><button onClick={onBack} className="mb-4 px-4 py-2 bg-blue-600 text-white rounded">← Volver</button><h2>Registro Diario</h2></div>;

export default function Dashboard() {
  const [screen, setScreen] = useState("dashboard");
  const [currentTime, setCurrentTime] = useState(new Date());

  const frases = [
    "La psicología correcta es el 90% del éxito en trading",
    "Domina tus emociones, domina el mercado",
    "La disciplina convierte estrategias en ganancias",
    "Un trader preparado mentalmente es imparable",
    "El control emocional es tu ventaja competitiva"
  ];
  const [fraseIndex, setFraseIndex] = useState(0);

  useEffect(() => {
    const intervalo = setInterval(() => {
      setFraseIndex((prev) => (prev + 1) % frases.length);
    }, 4000);
    return () => clearInterval(intervalo);
  }, []);

  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  // Navegación
  if (screen === "registro") return <RegistroDiario onBack={() => setScreen("dashboard")} />;
  if (screen === "progreso") return <ProgresoMental onBack={() => setScreen("dashboard")} />;
  if (screen === "fondeo") return <PruebaFondeo onBack={() => setScreen("dashboard")} />;

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-950 via-slate-900 to-indigo-950 text-white relative overflow-hidden">
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-blue-500/10 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-purple-500/10 rounded-full blur-3xl animate-pulse delay-1000"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-96 h-96 bg-indigo-500/5 rounded-full blur-3xl animate-pulse delay-500"></div>
      </div>
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_1px_1px,rgba(255,255,255,0.05)_1px,transparent_0)] bg-[size:20px_20px] pointer-events-none"></div>

      <div className="relative z-10 max-w-7xl mx-auto p-6">
        <header className="mb-12">
          <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between mb-8">
            <div>
              <div className="flex items-center gap-3 mb-4">
                <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-purple-600 rounded-xl flex items-center justify-center">
                  <Brain className="w-6 h-6 text-white" />
                </div>
                <div>
                  <h1 className="text-4xl lg:text-5xl font-bold bg-gradient-to-r from-blue-400 via-purple-400 to-cyan-400 bg-clip-text text-transparent">
                    PsicoTrading Pro
                  </h1>
                  <p className="text-slate-400 text-sm">Centro de Control Mental para Traders</p>
                </div>
              </div>
            </div>
            <div className="flex items-center gap-6">
              <div className="text-right">
                <p className="text-2xl font-mono font-bold text-blue-400">
                  {currentTime.toLocaleTimeString('es-AR', { timeZone: 'America/Argentina/Buenos_Aires' })}
                </p>
                <p className="text-sm text-slate-400">Buenos Aires</p>
              </div>
              <div className="w-16 h-16 bg-gradient-to-br from-green-500/20 to-blue-500/20 rounded-full flex items-center justify-center border border-green-500/30">
                <Activity className="w-8 h-8 text-green-400 animate-pulse" />
              </div>
            </div>
          </div>

          <div className="bg-gradient-to-r from-slate-800/50 to-slate-700/50 backdrop-blur-sm rounded-2xl p-6 border border-slate-600/30">
            <div className="flex items-center gap-3 mb-3">
              <Zap className="w-5 h-5 text-yellow-400" />
              <span className="text-sm font-medium text-slate-300">MENTALIDAD DEL DÍA</span>
            </div>
            <p className="text-xl lg:text-2xl font-medium text-white leading-relaxed transition-all duration-1000">
              {frases[fraseIndex]}
            </p>
          </div>
        </header>

        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-12">
          <StatCard icon={Trophy} label="Racha Actual" value="7 días" color="from-yellow-500 to-orange-500" />
          <StatCard icon={Target} label="Precisión Mental" value="84%" color="from-green-500 to-emerald-500" />
          <StatCard icon={BarChart3} label="Trades Analizados" value="156" color="from-blue-500 to-cyan-500" />
          <StatCard icon={Star} label="Nivel Disciplina" value="Elite" color="from-purple-500 to-pink-500" />
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 mb-12">
          <ModuleCard
            title="Prueba de Fondeo"
            description="Sistema avanzado de evaluación y seguimiento de fondeo"
            icon={TrendingUp}
            features={["Análisis en tiempo real", "Métricas avanzadas", "Seguimiento de progreso"]}
            onClick={() => setScreen("fondeo")}
            gradient="from-blue-600 to-cyan-500"
            highlight="NUEVO"
          />
          <ModuleCard
            title="Registro Diario"
            description="Journaling inteligente para traders profesionales"
            icon={BookOpen}
            features={["Auto-análisis emocional", "Patrones de comportamiento", "Insights personalizados"]}
            onClick={() => setScreen("registro")}
            gradient="from-purple-600 to-pink-500"
          />
          <ModuleCard
            title="Progreso Mental"
            description="Dashboard completo de evolución psicológica"
            icon={Brain}
            features={["Métricas psicológicas", "Tendencias emocionales", "Objetivos mentales"]}
            onClick={() => setScreen("progreso")}
            gradient="from-emerald-600 to-teal-500"
          />
        </div>

        <div className="bg-gradient-to-r from-slate-800/30 to-slate-700/30 backdrop-blur-sm rounded-3xl p-8 border border-slate-600/20">
          <div className="flex items-center gap-3 mb-6">
            <Eye className="w-6 h-6 text-indigo-400" />
            <h2 className="text-2xl font-bold text-white">Herramientas Adicionales</h2>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <ToolCard icon={Clock} title="Timer de Trading" desc="Gestión de tiempo" />
            <ToolCard icon={Activity} title="Monitor Emocional" desc="Estado en tiempo real" />
            <ToolCard icon={Target} title="Objetivos Diarios" desc="Metas y seguimiento" />
            <ToolCard icon={BarChart3} title="Analytics Pro" desc="Métricas avanzadas" />
          </div>
        </div>
      </div>
    </div>
  );
}

function StatCard({ icon: Icon, label, value, color }) {
  return (
    <div className="bg-slate-800/50 backdrop-blur-sm rounded-2xl p-6 border border-slate-600/30 hover:border-slate-500/50 transition-all duration-300 group">
      <div className="flex items-center justify-between mb-4">
        <div className={`w-12 h-12 bg-gradient-to-r ${color} rounded-xl flex items-center justify-center group-hover:scale-110 transition-transform duration-300`}>
          <Icon className="w-6 h-6 text-white" />
        </div>
      </div>
      <p className="text-3xl font-bold text-white mb-1">{value}</p>
      <p className="text-sm text-slate-400">{label}</p>
    </div>
  );
}

function ModuleCard({ title, description, icon: Icon, features, onClick, gradient, highlight }) {
  return (
    <div 
      onClick={onClick}
      className="group bg-slate-800/30 backdrop-blur-sm rounded-3xl p-8 border border-slate-600/30 hover:border-slate-500/50 cursor-pointer transition-all duration-500 hover:transform hover:scale-105 hover:shadow-2xl hover:shadow-blue-500/20 relative overflow-hidden"
    >
      <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/5 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-1000"></div>
      {highlight && (
        <div className="absolute top-4 right-4 bg-gradient-to-r from-yellow-500 to-orange-500 text-xs font-bold px-3 py-1 rounded-full text-black">
          {highlight}
        </div>
      )}
      <div className="relative z-10">
        <div className={`w-16 h-16 bg-gradient-to-r ${gradient} rounded-2xl flex items-center justify-center mb-6 group-hover:rotate-12 transition-transform duration-300`}>
          <Icon className="w-8 h-8 text-white" />
        </div>
        <h3 className="text-2xl font-bold text-white mb-3 group-hover:text-blue-300 transition-colors duration-300">
          {title}
        </h3>
        <p className="text-slate-400 mb-6 leading-relaxed">{description}</p>
        <ul className="space-y-2 mb-6">
          {features.map((feature, index) => (
            <li key={index} className="flex items-center gap-2 text-sm text-slate-300">
              <div className="w-1.5 h-1.5 bg-blue-400 rounded-full"></div>
              {feature}
            </li>
          ))}
        </ul>
        <div className="flex items-center gap-2 text-blue-400 font-medium group-hover:gap-4 transition-all duration-300">
          <span>Acceder al módulo</span>
          <ArrowRight className="w-4 h-4 group-hover:translate-x-1 transition-transform duration-300" />
        </div>
      </div>
    </div>
  );
}

function ToolCard({ icon: Icon, title, desc }) {
  return (
    <div className="bg-slate-700/30 rounded-xl p-4 border border-slate-600/20 hover:border-slate-500/40 transition-all duration-300 cursor-pointer group">
      <div className="flex items-center gap-3 mb-2">
        <div className="w-8 h-8 bg-indigo-500/20 rounded-lg flex items-center justify-center group-hover:bg-indigo-500/30 transition-colors duration-300">
          <Icon className="w-4 h-4 text-indigo-400" />
        </div>
        <h4 className="font-semibold text-white text-sm">{title}</h4>
      </div>
      <p className="text-xs text-slate-400">{desc}</p>
    </div>
  );
}
