import React, { useState } from "react";

export default function Emotions({ onBack }) {
  const [form, setForm] = useState({
    antes: "",
    durante: "",
    despues: "",
    comentario: "",
  });

  const emociones = ["Calma", "Ansiedad", "Impulsividad", "Confianza", "Miedo", "Frustraci√≥n"];

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

 const handleSubmit = (e) => {
  e.preventDefault();
  const registrosAnteriores = JSON.parse(localStorage.getItem("registrosEmocionales") || "[]");
  const nuevoRegistro = { ...form, timestamp: new Date().toISOString() };
  localStorage.setItem("registrosEmocionales", JSON.stringify([...registrosAnteriores, nuevoRegistro]));
  alert("‚úÖ Registro emocional guardado");
  setForm({ antes: "", durante: "", despues: "", comentario: "" });
};


  return (
    <div className="min-h-screen bg-gradient-to-b from-red-50 to-orange-100 p-6">
      <div className="max-w-2xl mx-auto bg-white rounded-xl shadow-md p-6">
        <button onClick={onBack} className="text-red-600 hover:text-red-800 mb-4 text-sm">
          ‚Üê Volver al Dashboard
        </button>
        <h2 className="text-2xl font-bold text-orange-700 mb-4">üß† Registro de Emociones por Trade</h2>

        <form onSubmit={handleSubmit} className="space-y-4">
          {["antes", "durante", "despues"].map((fase) => (
            <div key={fase}>
              <label className="block font-medium capitalize text-gray-700 mb-1">
                Emoci√≥n {fase} del trade:
              </label>
              <select
                name={fase}
                value={form[fase]}
                onChange={handleChange}
                className="w-full border rounded p-2"
                required
              >
                <option value="">Selecciona una emoci√≥n</option>
                {emociones.map((emo) => (
                  <option key={emo} value={emo}>
                    {emo}
                  </option>
                ))}
              </select>
            </div>
          ))}

          <div>
            <label className="block font-medium text-gray-700 mb-1">Comentario breve (opcional):</label>
            <textarea
              name="comentario"
              value={form.comentario}
              onChange={handleChange}
              className="w-full border rounded p-2"
              rows={3}
              placeholder="¬øQu√© notaste durante esta operaci√≥n?"
            />
          </div>

          <button
            type="submit"
            className="bg-orange-500 text-white px-4 py-2 rounded hover:bg-orange-600 transition"
          >
            Guardar registro
          </button>
        </form>
      </div>
    </div>
  );
}
