import React, { useState } from "react";
import PreMarket from "./PreMarket";
import Emotions from "./Emotions";
import { ArrowRight } from "lucide-react"; // icono de flecha elegante (opcional)

export default function Dashboard() {
  const [screen, setScreen] = useState("dashboard");

  if (screen === "premarket")
    return <PreMarket onBack={() => setScreen("dashboard")} />;
  if (screen === "emociones")
    return <Emotions onBack={() => setScreen("dashboard")} />;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-indigo-200 p-6">
      <div className="max-w-5xl mx-auto">
        <h1 className="text-4xl font-bold text-indigo-900 mb-8 text-center">
          🧠 MentalTrading Dashboard
        </h1>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          <Card
            title="🧘 Rutina Pre-Mercado"
            onClick={() => setScreen("premarket")}
          />
          <Card
            title="😌 Registro de Emociones"
            onClick={() => setScreen("emociones")}
          />
          <Card
            title="📓 Diario de Trading"
            onClick={() => alert("Próximamente")}
          />
          <Card
            title="📈 Progreso Mental"
            onClick={() => alert("Próximamente")}
          />
          <Card
            title="💡 Inspiración Diaria"
            onClick={() => alert("Próximamente")}
          />
        </div>
      </div>
    </div>
  );
}

function Card({ title, onClick }) {
  return (
    <div
      onClick={onClick}
      className="bg-white hover:shadow-lg transition-all rounded-2xl p-6 cursor-pointer shadow-md flex flex-col justify-between h-full"
    >
      <h2 className="text-xl font-semibold text-indigo-800 mb-2">{title}</h2>
      <div className="flex items-center text-sm text-indigo-600 font-medium">
        Ir a módulo <ArrowRight className="ml-1 w-4 h-4" />
      </div>
    </div>
  );
}
