import React, { useState, useEffect } from "react";
import Fondeo from "./Fondeo";
import Emotions from "./Emotions";
import Progreso from "./Progreso";

export default function Dashboard() {
  const [screen, setScreen] = useState("dashboard");
  const frases = [
    "Bienvenido, este es el primer paso hacia una mente de trading fuerte.",
    "La disciplina te llevar치 m치s lejos que cualquier setup.",
    "Entrena tu mente, no solo tus entradas.",
    "No se trata de ganar hoy, sino de operar bien hoy.",
    "Tu progreso mental define tu rentabilidad a largo plazo.",
  ];

  const [fraseActual, setFraseActual] = useState(frases[0]);

  useEffect(() => {
    const intervalo = setInterval(() => {
      const random = Math.floor(Math.random() * frases.length);
      setFraseActual(frases[random]);
    }, 15000);
    return () => clearInterval(intervalo);
  }, []);

  if (screen === "fondeo") return <Fondeo onBack={() => setScreen("dashboard")} />;
  if (screen === "emociones") return <Emotions onBack={() => setScreen("dashboard")} />;
  if (screen === "progreso") return <Progreso onBack={() => setScreen("dashboard")} />;

  return (
    <div className="min-h-screen bg-gradient-to-b from-black to-gray-900 text-white p-6">
      <div className="max-w-5xl mx-auto">
        <h1 className="text-4xl font-bold mb-4 text-white">游늵 MentalTrading Dashboard</h1>
        <p className="text-lg text-gray-300 italic mb-8">{fraseActual}</p>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          <Card title="Prueba de Fondeo" onClick={() => setScreen("fondeo")} />
          <Card title="Registro Diario" onClick={() => setScreen("emociones")} />
          <Card title="Progreso Mental" onClick={() => setScreen("progreso")} />
        </div>
      </div>
    </div>
  );
}

function Card({ title, onClick }) {
  return (
    <div
      onClick={onClick}
      className="bg-gray-800 hover:bg-indigo-700 transition-all duration-200 rounded-xl p-5 cursor-pointer shadow-md hover:shadow-lg"
    >
      <h2 className="text-xl font-semibold text-white">{title}</h2>
      <p className="text-sm text-gray-400 mt-2">Ir a m칩dulo</p>
    </div>
  );
}
