import React, { useState } from "react";

export default function Fondeo({ config, onBack }) {
  const [trades, setTrades] = useState([]);
  const [pair, setPair] = useState("");
  const [result, setResult] = useState("");
  const [emotion, setEmotion] = useState("");
  const [comment, setComment] = useState("");

  if (!config) {
    return (
      <div className="p-6 text-white">
        <p>Error: configuraci√≥n no encontrada.</p>
        <button onClick={onBack} className="text-indigo-400 underline mt-4">
          Volver al Dashboard
        </button>
      </div>
    );
  }

  const riskAmount = ((config.accountSize * config.riskPercent) / 100).toFixed(2);
  const tradesToday = trades.length;
  const tradesExceeded = tradesToday >= config.tradesPerDay;

  const handleAddTrade = () => {
    if (!result || !pair) {
      alert("Por favor completa al menos el resultado y el par operado.");
      return;
    }

    const newTrade = {
      id: trades.length + 1,
      pair,
      result: parseFloat(result),
      emotion,
      comment,
      timestamp: new Date().toLocaleString(),
    };

    setTrades([newTrade, ...trades]);

    // Limpiar campos
    setResult("");
    setPair("");
    setEmotion("");
    setComment("");
  };

  const total = trades.reduce((acc, t) => acc + t.result, 0).toFixed(2);

  return (
    <div className="min-h-screen bg-black text-white p-6">
      <div className="max-w-5xl mx-auto">
        <button onClick={onBack} className="text-indigo-400 hover:underline mb-6 text-sm">
          ‚Üê Volver al Dashboard
        </button>

        <h1 className="text-3xl font-bold text-indigo-400 mb-2">üìà Prueba de Fondeo</h1>
        <p className="text-gray-400 mb-4">Riesgo sugerido por trade: <strong>${riskAmount}</strong></p>

        {tradesExceeded && (
          <div className="bg-red-800 text-red-200 p-3 rounded mb-4">
            Has excedido la cantidad de trades recomendados por d√≠a. ‚ö†Ô∏è Esto puede afectar tu psicolog√≠a.
          </div>
        )}

        <div className="grid md:grid-cols-2 gap-4 mb-8">
          <div>
            <label className="block text-sm mb-1 text-gray-300">Par Operado (ej: EUR/USD)</label>
            <input
              type="text"
              value={pair}
              onChange={(e) => setPair(e.target.value)}
              className="w-full p-2 rounded bg-gray-800 border border-gray-700"
              placeholder="Par operado"
            />
          </div>

          <div>
            <label className="block text-sm mb-1 text-gray-300">Resultado ($)</label>
            <input
              type="number"
              value={result}
              onChange={(e) => setResult(e.target.value)}
              className="w-full p-2 rounded bg-gray-800 border border-gray-700"
              placeholder="Ej: -100 o 250"
            />
          </div>

          <div>
            <label className="block text-sm mb-1 text-gray-300">Emoci√≥n principal</label>
            <input
              type="text"
              value={emotion}
              onChange={(e) => setEmotion(e.target.value)}
              className="w-full p-2 rounded bg-gray-800 border border-gray-700"
              placeholder="Confianza, miedo, duda, etc."
            />
          </div>

          <div>
            <label className="block text-sm mb-1 text-gray-300">Comentario</label>
            <input
              type="text"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              className="w-full p-2 rounded bg-gray-800 border border-gray-700"
              placeholder="¬øPor qu√© tomaste este trade?"
            />
          </div>
        </div>

        <button
          onClick={handleAddTrade}
          className="bg-indigo-600 hover:bg-indigo-700 text-white py-2 px-6 rounded mb-8"
        >
          Agregar Trade
        </button>

        <div className="bg-gray-900 p-4 rounded-xl border border-gray-700">
          <h2 className="text-xl font-semibold text-indigo-300 mb-4">üìä Historial de Trades</h2>
          <p className="mb-4 text-gray-400">Total acumulado: <strong className="text-white">${total}</strong></p>

          {trades.length === 0 ? (
            <p className="text-gray-500">A√∫n no has registrado ning√∫n trade.</p>
          ) : (
            <div className="space-y-3 max-h-80 overflow-y-auto">
              {trades.map((trade) => (
                <div key={trade.id} className="bg-gray-800 p-3 rounded border border-gray-700">
                  <p className="text-sm text-gray-400">#{trade.id} - {trade.timestamp}</p>
                  <p className="text-white">Par: {trade.pair} | Resultado: <strong className={trade.result >= 0 ? "text-green-400" : "text-red-400"}>${trade.result}</strong></p>
                  {trade.emotion && <p className="text-sm text-gray-400">Emoci√≥n: {trade.emotion}</p>}
                  {trade.comment && <p className="text-sm text-gray-500">Comentario: {trade.comment}</p>}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
