import React, { useState } from "react";

export default function PreMarket({ onBack }) {
  const [checklist, setChecklist] = useState({
    dormiBien: false,
    reviseNoticias: false,
    reviseSetup: false,
    estoyEnfocado: false,
  });

  const mantras = [
    "Opero solo cuando veo mi setup.",
    "Menos es m√°s. La paciencia paga.",
    "Mi trabajo es seguir el plan, no predecir el mercado.",
  ];

  const toggleItem = (key) => {
    setChecklist({ ...checklist, [key]: !checklist[key] });
  };

  return (
    <div className="min-h-screen bg-white p-6">
      <button onClick={onBack} className="text-indigo-600 hover:text-indigo-800 mb-4 text-sm">
        ‚Üê Volver al Dashboard
      </button>

      <h2 className="text-2xl font-bold text-indigo-700 mb-4">üßò Rutina Pre-Mercado</h2>

      <div className="bg-gray-100 rounded-xl p-4 mb-6">
        <h3 className="text-lg font-semibold mb-2">Checklist Mental</h3>
        <div className="space-y-2">
          <Checkbox
            label="Dorm√≠ bien"
            checked={checklist.dormiBien}
            onChange={() => toggleItem("dormiBien")}
          />
          <Checkbox
            label="Revis√© noticias econ√≥micas"
            checked={checklist.reviseNoticias}
            onChange={() => toggleItem("reviseNoticias")}
          />
          <Checkbox
            label="Tengo claro mi setup"
            checked={checklist.reviseSetup}
            onChange={() => toggleItem("reviseSetup")}
          />
          <Checkbox
            label="Estoy mentalmente enfocado"
            checked={checklist.estoyEnfocado}
            onChange={() => toggleItem("estoyEnfocado")}
          />
        </div>
      </div>

      <div className="bg-indigo-50 rounded-xl p-4">
        <h3 className="text-lg font-semibold mb-2">üß† Mantra del D√≠a</h3>
        <p className="text-indigo-800 italic">
          ‚Äú{mantras[Math.floor(Math.random() * mantras.length)]}‚Äù
        </p>
      </div>
    </div>
  );
}

function Checkbox({ label, checked, onChange }) {
  return (
    <label className="flex items-center space-x-3">
      <input
        type="checkbox"
        checked={checked}
        onChange={onChange}
        className="form-checkbox h-4 w-4 text-indigo-600"
      />
      <span className="text-gray-800">{label}</span>
    </label>
  );
}
