import React, { useState } from "react";

export default function FondeoSetup({ onSetupComplete }) {
  const [capital, setCapital] = useState("");
  const [riesgo, setRiesgo] = useState("");
  const [maxTrades, setMaxTrades] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      capital: parseFloat(capital),
      riesgo: parseFloat(riesgo),
      maxTrades: parseInt(maxTrades),
    };
    localStorage.setItem("fondeoSettings", JSON.stringify(data));
    onSetupComplete();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-indigo-200 flex items-center justify-center p-4">
      <form
        onSubmit={handleSubmit}
        className="bg-white p-6 rounded-xl shadow-md max-w-md w-full"
      >
        <h2 className="text-xl font-bold text-indigo-700 mb-4">Configuración Inicial</h2>

        <label className="block mb-3">
          Capital de la cuenta (USD)
          <input
            type="number"
            value={capital}
            onChange={(e) => setCapital(e.target.value)}
            required
            className="w-full mt-1 p-2 border rounded-md"
          />
        </label>

        <label className="block mb-3">
          % de riesgo por operación
          <input
            type="number"
            value={riesgo}
            onChange={(e) => setRiesgo(e.target.value)}
            required
            step="0.1"
            className="w-full mt-1 p-2 border rounded-md"
          />
        </label>

        <label className="block mb-4">
          Máximo de trades por día
          <input
            type="number"
            value={maxTrades}
            onChange={(e) => setMaxTrades(e.target.value)}
            required
            className="w-full mt-1 p-2 border rounded-md"
          />
        </label>

        <button
          type="submit"
          className="bg-indigo-600 text-white py-2 px-4 rounded-md hover:bg-indigo-700 w-full"
        >
          Guardar y continuar
        </button>
      </form>
    </div>
  );
}
