import React, { useState } from "react";
import PreMarket from "./PreMarket";
import Emotions from "./Emotions";

export default function Dashboard() {
  const [screen, setScreen] = useState("dashboard");

  if (screen === "premarket") return <PreMarket onBack={() => setScreen("dashboard")} />;
  if (screen === "emociones") return <Emotions onBack={() => setScreen("dashboard")} />;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-indigo-200 p-6">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold text-indigo-800 mb-6">📊 MentalTrading Dashboard</h1>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          <Card title="Rutina Pre-Mercado" onClick={() => setScreen("premarket")} />
          <Card title="Registro de Emociones" onClick={() => setScreen("emociones")} />
          <Card title="Diario de Trading" onClick={() => alert("Próximamente")} />
          <Card title="Progreso Mental" onClick={() => alert("Próximamente")} />
          <Card title="Inspiración Diaria" onClick={() => alert("Próximamente")} />
        </div>
      </div>
    </div>
  );
}

function Card({ title, onClick }) {
  return (
    <div
      onClick={onClick}
      className="bg-white hover:bg-indigo-50 transition rounded-xl p-5 cursor-pointer shadow-md"
    >
      <h2 className="text-xl font-semibold text-indigo-700">{title}</h2>
      <p className="text-sm text-gray-600 mt-2">Ir a módulo</p>
    </div>
  );
}
