import React, { useState } from "react";

export default function Fondeo({ onBack, config }) {
  const [entries, setEntries] = useState([]);
  const [amount, setAmount] = useState("");
  const [result, setResult] = useState("");
  const [emotion, setEmotion] = useState("");
  const [pair, setPair] = useState("");

  const maxTradesPerDay = config.tradesPerDay;
  const riskPerTrade = (config.accountSize * config.riskPercent) / 100;

  const today = new Date().toLocaleDateString();
  const todayTrades = entries.filter(e => e.date === today).length;

  const handleSubmit = (e) => {
    e.preventDefault();
    if (todayTrades >= maxTradesPerDay) {
      alert("Has excedido el número de trades recomendados para hoy. Esto podría afectar tu psicología.");
      return;
    }

    const newEntry = {
      date: today,
      amount: parseFloat(amount),
      result,
      emotion,
      pair,
    };
    setEntries([newEntry, ...entries]);
    setAmount("");
    setResult("");
    setEmotion("");
    setPair("");
  };

  const total = entries.reduce((sum, entry) => sum + entry.amount, 0);

  return (
    <div className="min-h-screen bg-black text-white p-6">
      <button onClick={onBack} className="text-sm text-indigo-400 hover:underline mb-4">← Volver al Dashboard</button>

      <h2 className="text-3xl font-bold mb-6">📘 Prueba de Fondeo</h2>

      <form
        onSubmit={handleSubmit}
        className="bg-gray-900 p-6 rounded-xl shadow-lg max-w-2xl mx-auto"
      >
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm mb-1">Monto ($)</label>
            <input
              type="number"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              className="w-full bg-gray-800 p-2 rounded text-white"
              required
            />
          </div>

          <div>
            <label className="block text-sm mb-1">Resultado</label>
            <select
              value={result}
              onChange={(e) => setResult(e.target.value)}
              className="w-full bg-gray-800 p-2 rounded text-white"
              required
            >
              <option value="">Seleccionar</option>
              <option value="win">Ganancia</option>
              <option value="loss">Pérdida</option>
              <option value="be">Break Even</option>
            </select>
          </div>

          <div>
            <label className="block text-sm mb-1">Par Operado</label>
            <input
              type="text"
              value={pair}
              onChange={(e) => setPair(e.target.value)}
              className="w-full bg-gray-800 p-2 rounded text-white"
              placeholder="Ej: EUR/USD"
              required
            />
          </div>

          <div>
            <label className="block text-sm mb-1">Emoción</label>
            <input
              type="text"
              value={emotion}
              onChange={(e) => setEmotion(e.target.value)}
              className="w-full bg-gray-800 p-2 rounded text-white"
              placeholder="Ej: nervioso, confiado..."
              required
            />
          </div>
        </div>

        <button
          type="submit"
          className="mt-6 bg-indigo-600 hover:bg-indigo-700 px-4 py-2 rounded text-white font-semibold w-full"
        >
          Registrar Trade
        </button>
      </form>

      <div className="max-w-2xl mx-auto mt-10 bg-gray-900 p-6 rounded-xl shadow">
        <h3 className="text-xl font-semibold mb-4">📊 Resumen</h3>
        <p>Trades hoy: {todayTrades} / {maxTradesPerDay}</p>
        <p className="mt-2">Balance total: <span className={total >= 0 ? "text-green-400" : "text-red-400"}>${total.toFixed(2)}</span></p>
        <p className="mt-1">Riesgo por trade: ${riskPerTrade.toFixed(2)}</p>
      </div>

      <div className="max-w-2xl mx-auto mt-10">
        <h3 className="text-xl font-semibold mb-4">📅 Historial de Trades</h3>
        <ul className="space-y-3">
          {entries.map((entry, idx) => (
            <li key={idx} className="bg-gray-800 p-4 rounded-lg">
              <div className="flex justify-between">
                <span className="text-sm text-gray-400">{entry.date}</span>
                <span className={entry.amount >= 0 ? "text-green-400" : "text-red-400"}>
                  ${entry.amount.toFixed(2)}
                </span>
              </div>
              <div className="text-sm">Resultado: {entry.result}</div>
              <div className="text-sm">Par: {entry.pair}</div>
              <div className="text-sm">Emoción: {entry.emotion}</div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
